(function () {
  'use strict';

  function escapeHtml(value) {
    return String(value)
      .replace(/&/g, '&amp;')
      .replace(/</g, '&lt;')
      .replace(/>/g, '&gt;')
      .replace(/"/g, '&quot;')
      .replace(/'/g, '&#39;');
  }

  function safeJsonParse(value) {
    try {
      return JSON.parse(value);
    } catch (_err) {
      return value;
    }
  }

  function coerceState(enabled) {
    return enabled ? 'checked' : 'unchecked';
  }

  function StitchInspector() {
    this.enabled = false;
    this.overlay = null;
    this.tooltip = null;
    this.currentElement = null;
    this.button = null;
    // 触摸支持
    this.touchTimer = null;
    this.touchTarget = null;
    this.isTouchDevice = 'ontouchstart' in window || navigator.maxTouchPoints > 0;

    this.createOverlay();
    this.createTooltip();
    this.createButton();
    this.bindEvents();
  }

  StitchInspector.prototype.createOverlay = function () {
    var overlay = document.createElement('div');
    overlay.id = 'stitch-inspector-overlay';
    overlay.setAttribute('data-stitch-inspector', 'true');
    overlay.style.cssText = [
      'position: fixed',
      'pointer-events: none',
      'border: 2px solid #2563eb',
      'background: rgba(37, 99, 235, 0.12)',
      'z-index: 99998',
      'display: none',
      'transition: all 0.08s ease'
    ].join(';');
    document.body.appendChild(overlay);
    this.overlay = overlay;
  };

  StitchInspector.prototype.createTooltip = function () {
    var tooltip = document.createElement('div');
    tooltip.id = 'stitch-inspector-tooltip';
    tooltip.setAttribute('data-stitch-inspector', 'true');
    tooltip.style.cssText = [
      'position: fixed',
      'background: #0f172a',
      'color: #e2e8f0',
      'padding: 12px 14px',
      'border-radius: 8px',
      'font-family: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace',
      'font-size: 12px',
      'z-index: 99999',
      'display: none',
      'max-width: 420px',
      'box-shadow: 0 10px 24px rgba(15, 23, 42, 0.35)'
    ].join(';');
    document.body.appendChild(tooltip);
    this.tooltip = tooltip;
  };

  StitchInspector.prototype.createButton = function () {
    if (document.getElementById('stitch-inspector-toggle')) {
      return;
    }

    var button = document.createElement('button');
    button.id = 'stitch-inspector-toggle';
    button.type = 'button';
    button.setAttribute('data-stitch-inspector', 'true');
    button.textContent = '○ Inspector';
    button.style.cssText = [
      'position: fixed',
      'right: 18px',
      'bottom: 18px',
      'z-index: 99997',
      'border: 1px solid rgba(15, 23, 42, 0.2)',
      'background: #ffffff',
      'color: #1e293b',
      'padding: 8px 12px',
      'border-radius: 999px',
      'font-size: 12px',
      'font-weight: 600',
      'box-shadow: 0 6px 18px rgba(15, 23, 42, 0.12)',
      'cursor: pointer'
    ].join(';');

    var self = this;
    button.addEventListener('click', function (event) {
      event.preventDefault();
      self.toggle();
    });

    document.body.appendChild(button);
    this.button = button;
  };

  StitchInspector.prototype.bindEvents = function () {
    var self = this;

    document.addEventListener('mousemove', function (event) {
      if (!self.enabled) {
        return;
      }

      var target = self.findTarget(event.target);
      if (target && target !== self.currentElement) {
        self.highlight(target);
        self.showTooltip(target, event);
        return;
      }

      if (!target) {
        self.hide();
      }
    });

    document.addEventListener('click', function (event) {
      if (!self.enabled) {
        return;
      }

      var target = self.findTarget(event.target);
      if (!target) {
        return;
      }

      event.preventDefault();
      event.stopPropagation();
      self.copyComponentInfo(target);
    });

    document.addEventListener('keydown', function (event) {
      if ((event.ctrlKey || event.metaKey) && event.shiftKey && (event.key === 'I' || event.key === 'i')) {
        event.preventDefault();
        self.toggle();
        return;
      }

      if (event.key === 'Escape' && self.enabled) {
        self.disable();
      }
    });

    window.addEventListener('scroll', function () {
      if (self.enabled && self.currentElement) {
        self.highlight(self.currentElement);
      }
    }, { passive: true });

    window.addEventListener('resize', function () {
      if (self.enabled && self.currentElement) {
        self.highlight(self.currentElement);
      }
    });

    // 触摸事件支持（手机端）
    document.addEventListener('touchstart', function (event) {
      if (!self.enabled) {
        return;
      }

      var touch = event.touches[0];
      var target = self.findTarget(document.elementFromPoint(touch.clientX, touch.clientY));

      if (!target) {
        self.hide();
        return;
      }

      self.touchTarget = target;

      // 长按 400ms 显示组件信息
      self.touchTimer = setTimeout(function () {
        self.highlight(target);
        self.showTooltipAtCenter(target);
      }, 400);
    }, { passive: true });

    document.addEventListener('touchmove', function (event) {
      // 移动时取消长按
      if (self.touchTimer) {
        clearTimeout(self.touchTimer);
        self.touchTimer = null;
      }
    }, { passive: true });

    document.addEventListener('touchend', function (event) {
      if (!self.enabled) {
        return;
      }

      // 清除长按计时器
      if (self.touchTimer) {
        clearTimeout(self.touchTimer);
        self.touchTimer = null;
      }

      // 如果当前有显示的组件，点击复制
      if (self.currentElement && self.touchTarget === self.currentElement) {
        event.preventDefault();
        self.copyComponentInfo(self.currentElement);
      }

      self.touchTarget = null;
    });
  };

  StitchInspector.prototype.findTarget = function (target) {
    if (!target || !(target instanceof Element)) {
      return null;
    }

    if (target.closest('[data-stitch-inspector]')) {
      return null;
    }

    return target.closest('[data-stitch-type]');
  };

  StitchInspector.prototype.parseComponentInfo = function (element) {
    var props = element.dataset.stitchProps ? safeJsonParse(element.dataset.stitchProps) : null;
    return {
      type: element.dataset.stitchType || 'Unknown',
      id: element.dataset.stitchId || null,
      path: element.dataset.stitchPath || null,
      props: props,
      depth: element.dataset.stitchDepth ? parseInt(element.dataset.stitchDepth, 10) : null,
      element: element
    };
  };

  StitchInspector.prototype.highlight = function (element) {
    this.currentElement = element;
    var rect = element.getBoundingClientRect();

    if (!this.overlay) {
      return;
    }

    this.overlay.style.display = 'block';
    this.overlay.style.top = rect.top + 'px';
    this.overlay.style.left = rect.left + 'px';
    this.overlay.style.width = rect.width + 'px';
    this.overlay.style.height = rect.height + 'px';
  };

  StitchInspector.prototype.showTooltip = function (element, event) {
    var info = this.parseComponentInfo(element);

    if (!this.tooltip) {
      return;
    }

    this.tooltip.innerHTML = this.renderTooltipContent(info);
    this.tooltip.style.display = 'block';

    var x = event.clientX + 14;
    var y = event.clientY + 14;
    var rect = this.tooltip.getBoundingClientRect();
    var maxX = window.innerWidth - rect.width - 12;
    var maxY = window.innerHeight - rect.height - 12;

    this.tooltip.style.left = Math.min(x, maxX) + 'px';
    this.tooltip.style.top = Math.min(y, maxY) + 'px';
  };

  // 手机端：在元素下方居中显示 tooltip
  StitchInspector.prototype.showTooltipAtCenter = function (element) {
    var info = this.parseComponentInfo(element);

    if (!this.tooltip) {
      return;
    }

    this.tooltip.innerHTML = this.renderTooltipContent(info, true);
    this.tooltip.style.display = 'block';

    var elemRect = element.getBoundingClientRect();
    var tooltipRect = this.tooltip.getBoundingClientRect();

    // 优先显示在元素下方
    var y = elemRect.bottom + 10;
    // 如果下方空间不够，显示在上方
    if (y + tooltipRect.height > window.innerHeight - 12) {
      y = elemRect.top - tooltipRect.height - 10;
    }
    // 如果上方也不够，固定在底部
    if (y < 12) {
      y = window.innerHeight - tooltipRect.height - 60;
    }

    // 水平居中
    var x = (window.innerWidth - tooltipRect.width) / 2;

    this.tooltip.style.left = Math.max(12, x) + 'px';
    this.tooltip.style.top = Math.max(12, y) + 'px';
  };

  StitchInspector.prototype.renderTooltipContent = function (info, isMobile) {
    var lines = [];

    lines.push('<div style="color:#93c5fd;font-weight:600;font-size:13px;margin-bottom:8px;">&lt;' + escapeHtml(info.type) + ' /&gt;</div>');

    if (info.id) {
      lines.push('<div style="margin-bottom:4px;"><span style="color:#94a3b8;">id:</span> <span style="color:#fbbf24;">"' + escapeHtml(info.id) + '"</span></div>');
    }

    if (info.path) {
      lines.push('<div style="margin-bottom:4px;"><span style="color:#94a3b8;">path:</span> <span style="color:#34d399;">' + escapeHtml(info.path) + '</span></div>');
    }

    if (info.props && typeof info.props === 'object') {
      var keys = Object.keys(info.props);
      if (keys.length) {
        lines.push('<div style="margin-bottom:4px;"><span style="color:#94a3b8;">props:</span></div>');
        for (var i = 0; i < keys.length; i++) {
          var key = keys[i];
          var value = info.props[key];
          lines.push('<div style="padding-left:12px;color:#e2e8f0;">' + escapeHtml(key) + ': <span style="color:#f472b6;">' + escapeHtml(JSON.stringify(value)) + '</span></div>');
        }
      }
    } else if (info.props) {
      lines.push('<div style="margin-bottom:4px;"><span style="color:#94a3b8;">props:</span> <span style="color:#f472b6;">' + escapeHtml(JSON.stringify(info.props)) + '</span></div>');
    }

    // 根据设备类型显示不同提示
    var hint = isMobile
      ? 'Tap to copy | Tap elsewhere to close'
      : 'Click to copy | ESC to exit | Ctrl+Shift+I to toggle';
    lines.push('<div style="margin-top:10px;padding-top:8px;border-top:1px solid #1e293b;color:#94a3b8;font-size:11px;">' + hint + '</div>');

    return lines.join('');
  };

  StitchInspector.prototype.copyComponentInfo = function (element) {
    var info = this.parseComponentInfo(element);
    var payload = {
      type: info.type
    };

    if (info.id) {
      payload.id = info.id;
    }

    if (info.props && typeof info.props === 'object') {
      payload.props = info.props;
    }

    var lines = [
      'Component: ' + info.type
    ];

    if (info.id) {
      lines.push('ID: ' + info.id);
    }

    if (info.path) {
      lines.push('Path: ' + info.path);
    }

    lines.push('Props: ' + JSON.stringify(payload, null, 2));

    var text = lines.join('\n');
    var self = this;

    this.copyText(text)
      .then(function () {
        self.showCopyFeedback('Copied component info');
      })
      .catch(function () {
        self.showCopyFeedback('Copy failed');
      });
  };

  StitchInspector.prototype.copyText = function (text) {
    if (navigator.clipboard && navigator.clipboard.writeText) {
      return navigator.clipboard.writeText(text);
    }

    return new Promise(function (resolve, reject) {
      try {
        var textarea = document.createElement('textarea');
        textarea.value = text;
        textarea.setAttribute('readonly', 'readonly');
        textarea.style.position = 'absolute';
        textarea.style.left = '-9999px';
        document.body.appendChild(textarea);
        textarea.select();
        var success = document.execCommand('copy');
        document.body.removeChild(textarea);
        if (success) {
          resolve();
        } else {
          reject(new Error('copy failed'));
        }
      } catch (err) {
        reject(err);
      }
    });
  };

  StitchInspector.prototype.showCopyFeedback = function (message) {
    var toast = document.createElement('div');
    toast.setAttribute('data-stitch-inspector', 'true');
    toast.textContent = message;
    toast.style.cssText = [
      'position: fixed',
      'top: 18px',
      'left: 50%',
      'transform: translateX(-50%)',
      'background: #0f172a',
      'color: #e2e8f0',
      'padding: 8px 14px',
      'border-radius: 999px',
      'font-size: 12px',
      'font-weight: 600',
      'z-index: 100000',
      'box-shadow: 0 8px 18px rgba(15, 23, 42, 0.25)'
    ].join(';');

    document.body.appendChild(toast);

    setTimeout(function () {
      if (toast && toast.parentNode) {
        toast.parentNode.removeChild(toast);
      }
    }, 1600);
  };

  StitchInspector.prototype.hide = function () {
    this.currentElement = null;
    if (this.overlay) {
      this.overlay.style.display = 'none';
    }
    if (this.tooltip) {
      this.tooltip.style.display = 'none';
    }
  };

  StitchInspector.prototype.enable = function () {
    this.enabled = true;
    document.body.style.cursor = 'crosshair';
    if (this.button) {
      this.button.setAttribute('data-state', coerceState(true));
      this.button.style.background = '#2563eb';
      this.button.style.color = '#ffffff';
      this.button.style.borderColor = '#1d4ed8';
      this.button.textContent = '● Inspector ON';
    }
    console.log('[Stitch Inspector] enabled');
  };

  StitchInspector.prototype.disable = function () {
    this.enabled = false;
    this.hide();
    document.body.style.cursor = '';
    if (this.button) {
      this.button.setAttribute('data-state', coerceState(false));
      this.button.style.background = '#ffffff';
      this.button.style.color = '#1e293b';
      this.button.style.borderColor = 'rgba(15, 23, 42, 0.2)';
      this.button.textContent = '○ Inspector';
    }
    console.log('[Stitch Inspector] disabled');
  };

  StitchInspector.prototype.toggle = function () {
    if (this.enabled) {
      this.disable();
    } else {
      this.enable();
    }
  };

  function initInspector() {
    if (window.StitchInspector) {
      return;
    }

    window.StitchInspector = new StitchInspector();
    console.log('[Stitch Inspector] ready. Press Ctrl+Shift+I to toggle.');
  }

  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initInspector);
  } else {
    initInspector();
  }
})();
