screens.dsl:
[SECTION: chest_hospital_home]
  { ClassName: "min-h-screen bg-gray-50 pb-20" }

  [SECTION: banner]
    { ClassName: "mx-4 mt-4 rounded-xl overflow-hidden" }
    [IMAGE: banner_img]
      { ClassName: "w-full h-44 object-cover" }
