  [SECTION: user_stats_card]
    { ClassName: "mx-4 mt-4 p-4 bg-white rounded-xl flex items-center justify-between" }
    [SECTION: user_left]
      { ClassName: "flex items-center gap-3" }
      [IMAGE: avatar]
        { ClassName: "w-12 h-12 rounded-full" }
