      [TEXT: greeting]
        { ClassName: "text-lg font-semibold" }
        CONTENT: "你好，xkyy001"
    [SECTION: stats]
      { ClassName: "flex items-center gap-6" }
      [SECTION: steps]
        { ClassName: "text-center" }
        [TEXT: steps_label]
          { ClassName: "text-xs text-gray-500" }
