screens.dsl:
[SECTION: chest_hospital_home]
  { ClassName: "min-h-screen bg-gray-50" }

  [SECTION: header]
    { ClassName: "bg-white px-4 py-3 flex items-center justify-between" }
    [TEXT: app_title]
      { ClassName: "text-lg font-semibold" }
      CONTENT: "胸科医院"

  [SECTION: banner]
    { ClassName: "mx-4 mt-4 rounded-lg overflow-hidden" }
    [IMAGE: banner_image]
      { ClassName: "w-full h-40 object-cover" }
      ATTR: Src("/figma-images/13_42.png"), Alt("首页轮播图")

  [SECTION: user_card]
    { ClassName: "mx-4 mt-4 p-4 bg-white rounded-lg flex items-center gap-3" }
    [IMAGE: avatar]
      { ClassName: "w-12 h-12 rounded-full" }
      ATTR: Src("/figma-images/13_53.png"), Alt("用户头像")
    [SECTION: user_info]
      [TEXT: username]
        { ClassName: "font-medium text-base" }
        CONTENT: "xkyy0325"
      [TEXT: activity]
        { ClassName: "text-sm text-gray-500" }
        CONTENT: "活跃度: 20"

  [SECTION: stats]
    { ClassName: "mx-4 mt-4 p-3 bg-white rounded-lg flex justify-around" }
    [SECTION: stat_item]
      [TEXT: steps]
        { ClassName: "text-2xl font-bold text-center" }
        CONTENT: "3098"
      [TEXT: steps_label]
        { ClassName: "text-xs text-gray-500 text-center" }
        CONTENT: "步"

  [SECTION: feature_grid]
    { ClassName: "mx-4 mt-4 grid grid-cols-3 gap-2" }
    [CARD: welfare]
      { ClassName: "bg-white p-3 rounded-lg text-center" }
      [IMAGE: welfare_icon]
        { ClassName: "w-8 h-8 mx-auto" }
        ATTR: Src("/figma-images/17_44.png"), Alt("福利平台图标")
      [TEXT: welfare_text]
        { ClassName: "text-xs mt-1" }
        CONTENT: "福利平台"
    [CARD: member]
      { ClassName: "bg-white p-3 rounded-lg text-center" }
      [IMAGE: member_icon]
        { ClassName: "w-8 h-8 mx-auto" }
        ATTR: Src("/figma-images/17_43.png"), Alt("会员登记图标")
      [TEXT: member_text]
        { ClassName: "text-xs mt-1" }
        CONTENT: "会员登记"
    [CARD: news]
      { ClassName: "bg-white p-3 rounded-lg text-center" }
      [TEXT: news_text]
        { ClassName: "text-xs mt-1" }
        CONTENT: "工会动态"
    [CARD: help]
      { ClassName: "bg-white p-3 rounded-lg text-center" }
      [TEXT: help_text]
        { ClassName: "text-xs mt-1" }
        CONTENT: "困难帮扶"
    [CARD: org]
      { ClassName: "bg-white p-3 rounded-lg text-center" }
      [TEXT: org_text]
        { ClassName: "text-xs mt-1" }
        CONTENT: "组织机构"
    [CARD: congress]
      { ClassName: "bg-white p-3 rounded-lg text-center" }
      [TEXT: congress_text]
        { ClassName: "text-xs mt-1" }
        CONTENT: "职代会"
    [CARD: home]
      { ClassName: "bg-white p-3 rounded-lg text-center" }
      [TEXT: home_text]
        { ClassName: "text-xs mt-1" }
        CONTENT: "职工之家"
    [CARD: profile]
      { ClassName: "bg-white p-3 rounded-lg text-center" }
      [TEXT: profile_text]
        { ClassName: "text-xs mt-1" }
        CONTENT: "个人中心"
    [CARD: market]
      { ClassName: "bg-white p-3 rounded-lg text-center" }
      [TEXT: market_text]
        { ClassName: "text-xs mt-1" }
        CONTENT: "胸科闲余"

  [SECTION: news_section]
    { ClassName: "mx-4 mt-4 p-4 bg-white rounded-lg" }
    [TEXT: news_title]
      { ClassName: "text-lg font-semibold mb-3" }
      CONTENT: "新闻专区"
    [CARD: news_1]
      { ClassName: "flex gap-3 p-3 border rounded-lg" }
      [IMAGE: news_1_image]
        { ClassName: "w-16 h-16 rounded object-cover" }
        ATTR: Src("/figma-images/15_229.png"), Alt("新闻图片1")
      [SECTION: news_1_content]
        [TEXT: news_1_title]
          { ClassName: "font-medium text-sm" }
          CONTENT: "新闻政策"
        [TEXT: news_1_desc]
          { ClassName: "text-xs text-gray-500" }
          CONTENT: "关注时事热点"
    [CARD: news_2]
      { ClassName: "flex gap-3 p-3 border rounded-lg mt-2" }
      [IMAGE: news_2_image]
        { ClassName: "w-16 h-16 rounded object-cover" }
        ATTR: Src("/figma-images/15_238.png"), Alt("新闻图片2")
      [SECTION: news_2_content]
        [TEXT: news_2_title]
          { ClassName: "font-medium text-sm" }
          CONTENT: "劳模风采"
        [TEXT: news_2_desc]
          { ClassName: "text-xs text-gray-500" }
          CONTENT: "彰显榜样力量"
